import React, { useState } from "react";

function Signup() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(`Submitted: ${firstName} ${lastName}, ${email}, ${password}`);
  };

  return (
    <div className="w-full flex justify-center min-h-screen item-center">
      <div className="w-6/12 flex justify-center flex-col items-center pr-32 pl-32">
          <form onSubmit={handleSubmit}>
            <label>
              Name:
              <input
                type="text"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
              />
            </label>
            <br />
            <label>
              Email/Phone Number
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </label>
            <button type="submit">Verify with OTP</button>
          </form>
      </div>
    </div>
  );
}

export default Signup;
