import { Formik } from 'formik';
import React, { useRef } from 'react';
import { useDispatch } from 'react-redux';
import { loginFormSchema } from '../../validation/schema';
import InputBox from '../../components/formikFields/InputBox/InputBox';
import PasswordInput from '../../components/formikFields/PasswordInput/PasswordInput';

import {
  Go, Eye,
} from '../../assets/images';

const EyeIcon = (props: any) => {
  const { className } = props;
  return <img className={`mr-4 pr-1 cursor-pointer ${className}`} src={Eye} alt="" />;
};

const initialValues = {
  email: '',
  password: '',
};

const Login = () => {
  const formikRef: any = useRef();
  const dispatch = useDispatch();
  const onSubmit = (values: typeof initialValues) => {
    dispatch(authSlice.actions.signinUser({ password: values.password, email: values.email }));
  };

  return (
    <Formik
      innerRef={formikRef}
      initialValues={initialValues}
      onSubmit={onSubmit}
      validationSchema={loginFormSchema}
    >
      {({
        handleSubmit, values,
      }) => (
        <div className="w-full flex justify-center min-h-screen item-center">
          <div className="w-6/12 flex justify-center flex-col items-center pr-32 pl-32">
            <p className="f-28 font-semibold text-left w-full mb-8">
              Sign in to your account
            </p>
            <InputBox name="email" label="Email" placeholder="Enter email ID" isRequired={true} />
            <PasswordInput
              name="password"
              label="Password"
              placeholder="Enter password"
              righticon={EyeIcon}
              type="password"
              onKeyDown={(e: any) => {
                if (e.key === 'Enter') {
                  handleSubmit();
                }
              }}
            />
            <button
              type="button"
              onClick={() => {
                if (values.email && values.password) handleSubmit();
              }}
              className={(values.email && values.password) ? 'relative cursor-pointer w-full rounded-lg mt-4 bg-blue-710 hover:bg-white text-white text-blue-710hover' : 'relative cursor-not-allowed w-full rounded-lg mt-4 bg-gray-400 text-white'}
            >
              <div className={(values.email && values.password) ? 'w-full py-3 justify-center items-center flex rounded-lg border-blue-710 border font-bold uppercase duration-700' : 'w-full py-3 justify-center items-center flex rounded-lg border font-bold uppercase duration-700'}>Sign In</div>
              {(values.email && values.password) && (
              <div className="absolute flex items-center justify-center w-12 right-0 top-0 h-full">
                <img src={Go} alt="go" />
              </div>
              )}
            </button>
          </div>
        </div>
      )}
    </Formik>
  );
};

export default Login;
